import { createContext, useEffect, useState } from "react";
import { SubmitHandler } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { api } from "../../Services/api";
export {};

interface IContextCartProps {
  submitSearch: (data: ISearch) => void;
  handleExit: () => void;
}

interface iChildren {
  children: React.ReactNode;
}
interface ISearch {
  item: string;
}
export interface IProducts {
  id: number;
  name: string;
  category: string;
  price: number;
  img: string;
}

export const CartContexts = createContext({} as IContextCartProps);

export const CartProvider = ({ children }: iChildren) => {
  const [product, setProduct] = useState<null | IProducts>(null);
  const navigate = useNavigate();
  const submitSearch: SubmitHandler<ISearch> = (data) => {
    console.log(data);
  };
  const handleExit = () => {
    window.localStorage.clear();
    navigate("/");
  };
  useEffect(() => {
    async function loaderProducts() {
      const token = window.localStorage.getItem("@TOKENHAMBURGUEIRA");
      /* const id = window.localStorage.getItem("@IDUSERHAMBURGUEIRA"); */
      /* if (!token) {
        setUserLoading(false);
        return;
      } */
      try {
        const { data } = await api.get<IProducts>("/products", {
          headers: {
            authorization: `Bearer ${token}`,
          },
        });

        setProduct(data);
      } catch (error) {
        console.error(error);
      } /* finally {
        setUserLoading(false);
      } */
    }
    loaderProducts();
  }, []);
  return (
    <CartContexts.Provider value={{ submitSearch, handleExit }}>
      {children}
    </CartContexts.Provider>
  );
};
