import { LogoMaster } from "../../Components/Logo";
import { MainLogin } from "./style";
import { Link } from "react-router-dom";
import { Inputs } from "../../Components/Inputs";
import {
  useForm,
  SubmitHandler,
  UseFormRegister,
  FieldValues,
} from "react-hook-form";

import { yupResolver } from "@hookform/resolvers/yup";
import { formShemaLogin } from "./ShemaLogin";
import { StyledPErrors } from "../../Components/Inputs/style";
import { useContext } from "react";
import { UserContexts } from "../../Contexts/UserContexts/UserContexts";

interface ILoginPage {
  email?: string;
  password?: string;
  register?: UseFormRegister<FieldValues>;
}

export const LoginPage = () => {
  const { loading, submitLogin } = useContext(UserContexts);
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<ILoginPage>({
    resolver: yupResolver(formShemaLogin),
  });
  /*  const submitLogin: SubmitHandler<ILoginPage> = async (data) => {
    console.log(data);
  }; */

  return (
    <MainLogin>
      <section>
        <h2>Login</h2>
        <form onSubmit={handleSubmit(submitLogin)}>
          <Inputs text="Nome" chave="email" type="email" register={register} />
          {errors.email && (
            <StyledPErrors>{errors.email.message}</StyledPErrors>
          )}
          <Inputs
            text="Senha"
            type="password"
            chave="password"
            register={register}
          />
          {errors.password && (
            <StyledPErrors>{errors.password.message}</StyledPErrors>
          )}
          <button type="submit">Logar</button>
        </form>
        <p>Crie sua conta para saborear muitas del√≠cias e matar sua fome!</p>
        <Link to="/register">Cadastrar</Link>
      </section>
      <LogoMaster />
    </MainLogin>
  );
};
