import { StyledBoxModal, StyledModal } from "./style";
import { VscChromeClose } from "react-icons/vsc";
import { useContext } from "react";
import { UserContexts } from "../../Contexts/UserContexts/UserContexts";
import { LoadingComp } from "../Loading";
import burguer from "../../Assets/burguer.svg";
import lixo from "../../Assets/lixo.svg";
import { CartContexts } from "../../Contexts/CartContexts/CartContexts";

export const Modal = () => {
  const { loading, setOpenModal } = useContext(UserContexts);
  const { item, setItem, counter, setCounter } = useContext(CartContexts);
  const sum = () => {
    setCounter(counter + 1);
  };
  const sub = () => {
    setCounter(counter - 1);
  };
  return (
    <StyledModal>
      {loading && <LoadingComp />}
      <StyledBoxModal>
        <header>
          <h2>Carrinho de Compras</h2>
          <button onClick={() => setOpenModal(false)}>
            <VscChromeClose />
          </button>
        </header>

        {item.length !== 0 ? (
          <>
            <ul>
              {item.map((product) => (
                <li>
                  <div>
                    <div>
                      <img src={product.img} alt="" />
                    </div>
                    <div>
                      <p>{product.name}</p>
                      <div>
                        <button type="button" onClick={sum}>
                          +
                        </button>
                        <span>{counter}</span>
                        <button type="button" onClick={sub}>
                          -
                        </button>
                      </div>
                    </div>
                  </div>
                  <button>
                    <img src={lixo} alt="lixo" />
                  </button>
                </li>
              ))}
            </ul>
            <div>
              <p>Total</p>
              <span>R$ 14,00</span>
            </div>
            <button onClick={() => setItem([])}>Remover todos</button>
          </>
        ) : (
          <section>
            <p>Sua sacola est√° vazia</p>
            <p>Adicione itens</p>
          </section>
        )}
      </StyledBoxModal>
    </StyledModal>
  );
};
