import { useContext, useEffect } from "react";
import { Header } from "../../Components/Header";
import { ListProducts } from "../../Components/ListProducts";
import { MainContainer } from "../../Components/MainContainer";
import { Modal } from "../../Components/Modal";
import { CartContexts } from "../../Contexts/CartContexts/CartContexts";
import {
  IGetAutorization,
  UserContexts,
} from "../../Contexts/UserContexts/UserContexts";
import { api } from "../../Services/api";

export const Home = () => {
  const { product } = useContext(CartContexts);
  const { openModal, setUserLoading, setUser } = useContext(UserContexts);
  useEffect(() => {
    async function loaderUser() {
      const token = window.localStorage.getItem("@TOKENHAMBURGUEIRA");
      const id = window.localStorage.getItem("@IDUSERHAMBURGUEIRA");
      if (!token) {
        setUserLoading(false);
        return;
      }
      try {
        const { data } = await api.get<IGetAutorization>(`/users/${id}`, {
          headers: {
            authorization: `Bearer ${token}`,
          },
        });

        setUser(data);
      } catch (error) {
        window.localStorage.clear();
        console.error(error);
      } finally {
        setUserLoading(false);
      }
    }
    loaderUser();
  }, [setUser, setUserLoading]);
  return (
    <>
      {openModal && <Modal />}
      <Header />
      <MainContainer>
        <ul>
          {product.map((item) => (
            <ListProducts
              name={item.name}
              id={item.id}
              price={item.price}
              category={item.category}
              img={item.img}
              product={item}
            />
          ))}
        </ul>
      </MainContainer>
    </>
  );
};
