import { useContext, useEffect, useState } from "react";
import { CartContexts } from "../../Contexts/CartContexts/CartContexts";
import { IProducts } from "../../Services/getProducts";
import { StyledProductCardLi } from "./style";
interface IPropsCard extends IProducts {
  product: IProducts;
}

export const ListProducts = ({
  name,
  category,
  id,
  price,
  img,
  product,
}: IPropsCard) => {
  /* const [abobrinha, setabobrinha] = useState([] as IProducts[]); */
  const { abobrinha, setabobrinha } = useContext(CartContexts);

  function carrinho() {
    console.log(product);
    console.log(abobrinha);

    return setabobrinha([...abobrinha, product]);
  }

  return (
    <StyledProductCardLi key={id}>
      <img src={img} alt="" />

      <h3>{name}</h3>
      <p>{category}</p>
      <span>
        {price.toLocaleString("pt-BR", {
          style: "currency",
          currency: "BRL",
        })}
      </span>
      <button onClick={carrinho}>Adicionar</button>
    </StyledProductCardLi>
  );
};
