import { useEffect, useState } from "react";
import { IProducts } from "../../Services/getProducts";
import { StyledProductCardLi } from "./style";
interface IPropsCard extends IProducts {
  product: IProducts;
}

export const ListProducts = ({
  name,
  category,
  id,
  price,
  img,
  product,
}: IPropsCard) => {
  const [item, setItem] = useState([] as IProducts[]);

  useEffect(() => {
    function set() {
      setItem([...item, product]);
    }
  }, [item, product]);

  return (
    <StyledProductCardLi key={id}>
      <img src={img} alt="" />

      <h3>{name}</h3>
      <p>{category}</p>
      <span>
        {price.toLocaleString("pt-BR", {
          style: "currency",
          currency: "BRL",
        })}
      </span>
      <button onClick={() => set()}>Adicionar</button>
    </StyledProductCardLi>
  );
};
