import { IProducts } from "../../Services/getProducts";
import { StyledProductCardLi } from "./style";

export const ListProducts = ({ name, category, id, price, img }: IProducts) => {
  return (
    <StyledProductCardLi key={id}>
      <img src={img} alt="" />

      <h3>{name}</h3>
      <p>{category}</p>
      <span>
        {price.toLocaleString("pt-BR", {
          style: "currency",
          currency: "BRL",
        })}
      </span>
      <button>Adicionar</button>
    </StyledProductCardLi>
  );
};
