import { useContext, useEffect, useState } from "react";
import { CartContexts } from "../../Contexts/CartContexts/CartContexts";
import { IProducts } from "../../Services/getProducts";
import { StyledProductCardLi } from "./style";
interface IPropsCard extends IProducts {
  key: number;
  product: IProducts;
}

export const ListProducts = ({
  name,
  category,

  price,
  img,
  product,
}: IPropsCard) => {
  const { item, setItem } = useContext(CartContexts);

  function carrinho() {
    return setItem([...item, product]);
  }

  console.log(item);

  return (
    <StyledProductCardLi>
      <img src={img} alt="" />

      <h3>{name}</h3>
      <p>{category}</p>
      <span>
        {price.toLocaleString("pt-BR", {
          style: "currency",
          currency: "BRL",
        })}
      </span>
      <button onClick={carrinho}>Adicionar</button>
    </StyledProductCardLi>
  );
};
