import { useContext, useEffect, useState } from "react";
import { CartContexts } from "../../Contexts/CartContexts/CartContexts";
import { IProducts } from "../../Services/getProducts";
import { StyledProductCardLi } from "./style";
interface IPropsCard extends IProducts {
  key: number;
  product: IProducts;
}

interface ICarrinho extends IProducts {
  quantidade?: number;
}

export const ListProducts = ({
  name,
  category,

  price,
  img,
  product,
}: IPropsCard) => {
  const { item, setItem, setCounter, counter } = useContext(CartContexts);

  function carrinho(itemSelect: IProducts) {
    const newListCart = item.find((food) => {
      if (food.id === itemSelect.id) {
        let counter = food.quantidade + 1;
        return { ...food, quantidade: counter };
      } else {
        return { ...food, quantidade: 1 };
      }
    });
    if (item.length !== 0) {
      const productFind = item.find((food) => {
        return food.id === itemSelect.id;
      });
      if (productFind) {
        setCounter(counter + 1);
      } else {
        setItem([...item, product]);
      }
    } else {
      setItem([...item, product]);
    }
  }

  return (
    <StyledProductCardLi>
      <img src={img} alt="" />

      <h3>{name}</h3>
      <p>{category}</p>
      <span>
        {price.toLocaleString("pt-BR", {
          style: "currency",
          currency: "BRL",
        })}
      </span>
      <button onClick={() => carrinho(product)}>Adicionar</button>
    </StyledProductCardLi>
  );
};
