import * as Yup from "yup";

export const formShemaLogin = Yup.object().shape({
  name: Yup.string().required("Nome é obrigatório"),
  password: Yup.string()
    .required("Senha obrigatória")
    .matches(/.{8,}/, "A senha precisa conter no mínimo 8 caracteres"),
});
